    public function config($config) {
        MiwoShop::get('opencart')->get('config')->load($config);
    }

    public function language($language) {
        return MiwoShop::get('opencart')->get('language')->load($language);
    }

public function login($email, $password, $override = false) {
          $encrypted_password = MiwoShop::get('user')->getEncryptedOPassword($email, $password);

          $parts	            = explode(':', $encrypted_password);
          $encrypted_password2	= $parts[0];

          if ($override) {
              $customer_query = $this->db->query("SELECT * FROM " . DB_PREFIX . "customer where LOWER(email) = '" . $this->db->escape(strtolower($email)) . "' AND status = '1'");
          } else {
              $customer_query = $this->db->query("SELECT * FROM " . DB_PREFIX . "customer WHERE LOWER(email) = '" . $this->db->escape(strtolower($email)) . "' AND (password = SHA1(CONCAT(salt, SHA1(CONCAT(salt, SHA1('" . $this->db->escape($password) . "'))))) OR password = '" . $this->db->escape(md5($password)) . "' OR password = '".$this->db->escape($encrypted_password)."' OR password = '". $encrypted_password2 ."') AND status = '1' AND approved = '1'");
          }

        $file_name  = $this->filename;
        $trace      = debug_backtrace();

        if (!empty($trace)) {
            $file_path  = $trace[0]['file'];
            $file_path  = str_replace('\\', '/', $file_path);
            $file_path  = str_replace('.php', '', $file_path);

            $as_file    = strpos($file_path, str_replace('\\','/',$file_name));
            $as_vqmod   = strpos($file_path, str_replace('/','_',$file_name));
            $is_system  = strpos($file_path, 'system');

            if ($as_file === false && $as_vqmod === false && $is_system === false) {
                if (strpos($file_path, 'vq2-') !== false){
                    $_file_name = strstr($file_path, 'vq2-');
                    $path_array = explode('_', $_file_name);
                    unset($path_array[0]);
                    unset($path_array[1]);
                    $file_name = implode('/', $path_array);
                }
                else {
                    $_file_name = strstr($file_path, 'opencart');
                    $path_array = explode('/', $_file_name);
                    unset($path_array[0]);
                    unset($path_array[1]);
                    $file_name = implode('/', $path_array);
                }
            }
        }

        $string = 'COM_MIWOSHOP_'.strtoupper(str_replace('/', '_', $this->filename)).'_'.strtoupper($key);
		$text = MText::_($string);

		if ( ($text == $string or $text == '??'. $string .'??') and !MFactory::getApplication()->isAdmin() and !(isset($_GET['view']) and $_GET['view'] == 'admin') ) {
            $string = 'COM_MIWOSHOP_'.strtoupper(str_replace('/', '_', $file_name)).'_'.strtoupper($key);
            $text = MText::_($string);
        }
		
        if (isset($path_array[2]) and $path_array[2] == 'checkout' and ($text == $string or $text == '??'. $string .'??')) {
            $string = 'COM_MIWOSHOP_'.strtoupper(str_replace('/', '_', 'checkout/checkout')).'_'.strtoupper($key);
            $text = MText::_($string);
        }

        if ($text == $string or $text == '??'. $string .'??') {
            $string = 'COM_MIWOSHOP_'.strtoupper($key);
			$text = MText::_($string);
        }
		
		if (($text != $string) and ($text != '??'. $string .'??')) {
			return $text;
		}
		
		$lang = MFactory::getLanguage();
        $_directory =strtolower($lang->getTag());
        $_directory =str_replace('-', '_', $_directory);
        $text = $this->loadSecond($_directory, $this->filename, $key);

        if (!empty($text)) {
            return $text;
        }

public function load($filename) {
        $this->filename = $filename;

        $file = DIR_LANGUAGE . $this->default . '/' . $filename . '.php';
        if (file_exists($file)) {
            $_ = array();

            global $vqmod;
			require($file);

            $this->data = array_merge($this->data, $_);
        }

		$file = DIR_LANGUAGE . $this->directory . '/' . $filename . '.php';
		if (file_exists($file)) {
			$_ = array();

			global $vqmod;
			require($file);

			$this->data = array_merge($this->data, $_);
		}

        return $this->data;
  	}
	
	public function loadSecond($directory, $filename, $key) {
        $_ = array();

        $file = DIR_LANGUAGE . $directory . '/' . $filename . '.php';
        if (file_exists($file)) {
            global $vqmod;
			require(VQMod::modCheck($file));
        }

        return (isset($_[$key]) ? $_[$key] : '');
  	}	
	
	public function language($filename) { 
		return $this->load($filename);
	}

if (class_exists('Log')) {
	return;
}

